func product_of_digits(str: String) -> Int {
    var product = 1
    for char in str {
        let c = Int(String(char)) ?? 1
        product *= c
    }
    return product
}

func problem8(str: String, n: Int) -> Int {
    var max = 0
    for i in 0 ... str.count-n {
        let substr = str.substring(start: i, length: n)
        let product = product_of_digits(str: substr)
        if (product > max) {
            print("\(substr) --> \(product)")
            max = product
        }
    }
    print("max: \(max)")
    return max
}

func problem8(n: Int) -> Int{
    let heystack = ""
    
    return problem8(str: heystack, n: n)
}
